import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.9A-301 Law governing perfection and priority of security interests.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-301.md","filePath":"vacode/8.9A-301.md"}'),o={name:"vacode/8.9A-301.md"},n=i('<h1 id="§-8-9a-301-law-governing-perfection-and-priority-of-security-interests" tabindex="-1">§ 8.9A-301 Law governing perfection and priority of security interests. <a class="header-anchor" href="#§-8-9a-301-law-governing-perfection-and-priority-of-security-interests" aria-label="Permalink to &quot;§ 8.9A-301 Law governing perfection and priority of security interests.&quot;">​</a></h1><p>Except as otherwise provided in §§ <a href="http://law.lis.virginia.gov/vacode/8.9A-303/" target="_blank" rel="noreferrer">8.9A-303</a> through <a href="http://law.lis.virginia.gov/vacode/8.9A-306/" target="_blank" rel="noreferrer">8.9A-306</a>, the following rules determine the law governing perfection, the effect of perfection or nonperfection, and the priority of a security interest in collateral:</p><p>(1) Except as otherwise provided in this section, while a debtor is located in a jurisdiction, the local law of that jurisdiction governs perfection, the effect of perfection or nonperfection, and the priority of a security interest in collateral.</p><p>(2) While collateral is located in a jurisdiction, the local law of that jurisdiction governs perfection, the effect of perfection or nonperfection, and the priority of a possessory security interest in that collateral.</p><p>(3) Except as otherwise provided in paragraph (4), while tangible negotiable documents, goods, instruments, money, or tangible chattel paper are located in a jurisdiction, the local law of that jurisdiction governs:</p><p>(A) perfection of a security interest in the goods by filing a fixture filing;</p><p>(B) perfection of a security interest in timber to be cut; and</p><p>(C) the effect of perfection or nonperfection and the priority of a nonpossessory security interest in the collateral.</p><p>(4) The local law of the jurisdiction in which the wellhead or minehead is located governs perfection, the effect of perfection or nonperfection, and the priority of a security interest in as-extracted collateral.</p><p>Code 1950, § 55-9; 1964, c. 219, § 8.9-103; 1973, c. 509; 1984, c. 613; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>.</p>',10),a=[n];function c(s,l,p,f,h,d){return t(),r("div",null,a)}const _=e(o,[["render",c]]);export{u as __pageData,_ as default};
