import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.9A-304 Law governing perfection and priority of security interests in deposit accounts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-304.md","filePath":"vacode/8.9A-304.md"}'),a={name:"vacode/8.9A-304.md"},o=n('<h1 id="§-8-9a-304-law-governing-perfection-and-priority-of-security-interests-in-deposit-accounts" tabindex="-1">§ 8.9A-304 Law governing perfection and priority of security interests in deposit accounts. <a class="header-anchor" href="#§-8-9a-304-law-governing-perfection-and-priority-of-security-interests-in-deposit-accounts" aria-label="Permalink to &quot;§ 8.9A-304 Law governing perfection and priority of security interests in deposit accounts.&quot;">​</a></h1><p>(a) Law of bank&#39;s jurisdiction governs. The local law of a bank&#39;s jurisdiction governs perfection, the effect of perfection or nonperfection, and the priority of a security interest in a deposit account maintained with that bank.</p><p>(b) Bank&#39;s jurisdiction. The following rules determine a bank&#39;s jurisdiction for purposes of this part:</p><p>(1) If an agreement between the bank and the debtor governing the deposit account expressly provides that a particular jurisdiction is the bank&#39;s jurisdiction for purposes of this part, this title, or the Uniform Commercial Code, that jurisdiction is the bank&#39;s jurisdiction.</p><p>(2) If paragraph (1) does not apply and an agreement between the bank and its customer governing the deposit account expressly provides that the agreement is governed by the law of a particular jurisdiction, that jurisdiction is the bank&#39;s jurisdiction.</p><p>(3) If neither paragraph (1) nor paragraph (2) applies and an agreement between the bank and its customer governing the deposit account expressly provides that the deposit account is maintained at an office in a particular jurisdiction, that jurisdiction is the bank&#39;s jurisdiction.</p><p>(4) If none of the preceding paragraphs applies, the bank&#39;s jurisdiction is the jurisdiction in which the office identified in an account statement as the office serving the customer&#39;s account is located.</p><p>(5) If none of the preceding paragraphs applies, the bank&#39;s jurisdiction is the jurisdiction in which the chief executive office of the bank is located.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',9),r=[o];function s(c,p,d,h,f,u){return t(),i("div",null,r)}const _=e(a,[["render",s]]);export{g as __pageData,_ as default};
