import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 8.9A-310 When filing required to perfect security interest or agricultural lien; security interests and agricultural liens to which filing provisions do not apply.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-310.md","filePath":"vacode/8.9A-310.md"}'),a={name:"vacode/8.9A-310.md"},n=i('<h1 id="§-8-9a-310-when-filing-required-to-perfect-security-interest-or-agricultural-lien-security-interests-and-agricultural-liens-to-which-filing-provisions-do-not-apply" tabindex="-1">§ 8.9A-310 When filing required to perfect security interest or agricultural lien; security interests and agricultural liens to which filing provisions do not apply. <a class="header-anchor" href="#§-8-9a-310-when-filing-required-to-perfect-security-interest-or-agricultural-lien-security-interests-and-agricultural-liens-to-which-filing-provisions-do-not-apply" aria-label="Permalink to &quot;§ 8.9A-310 When filing required to perfect security interest or agricultural lien; security interests and agricultural liens to which filing provisions do not apply.&quot;">​</a></h1><p>(a) General rule; perfection by filing. Except as otherwise provided in subsection (b) and § <a href="http://law.lis.virginia.gov/vacode/8.9A-312/" target="_blank" rel="noreferrer">8.9A-312</a> (b), a financing statement must be filed to perfect all security interests and agricultural liens.</p><p>(b) Exceptions; filing not necessary. The filing of a financing statement is not necessary to perfect a security interest:</p><p>(1) that is perfected under § <a href="http://law.lis.virginia.gov/vacode/8.9A-308/" target="_blank" rel="noreferrer">8.9A-308</a> (d), (e), (f), or (g);</p><p>(2) that is perfected under § <a href="http://law.lis.virginia.gov/vacode/8.9A-309/" target="_blank" rel="noreferrer">8.9A-309</a> when it attaches;</p><p>(3) in property subject to a statute, regulation, or treaty described in § <a href="http://law.lis.virginia.gov/vacode/8.9A-311/" target="_blank" rel="noreferrer">8.9A-311</a> (a);</p><p>(4) in goods in possession of a bailee that is perfected under § <a href="http://law.lis.virginia.gov/vacode/8.9A-312/" target="_blank" rel="noreferrer">8.9A-312</a> (d) (1) or (2);</p><p>(5) in certificated securities, documents, goods, or instruments that is perfected without filing, control, or possession under § <a href="http://law.lis.virginia.gov/vacode/8.9A-312/" target="_blank" rel="noreferrer">8.9A-312</a> (e), (f), or (g);</p><p>(6) in collateral in the secured party&#39;s possession under § <a href="http://law.lis.virginia.gov/vacode/8.9A-313/" target="_blank" rel="noreferrer">8.9A-313</a>;</p><p>(7) in a certificated security which is perfected by delivery of the security certificate to the secured party under § <a href="http://law.lis.virginia.gov/vacode/8.9A-313/" target="_blank" rel="noreferrer">8.9A-313</a>;</p><p>(8) in deposit accounts, electronic chattel paper, electronic documents, investment property, or letter-of-credit rights which is perfected by control under § <a href="http://law.lis.virginia.gov/vacode/8.9A-314/" target="_blank" rel="noreferrer">8.9A-314</a>;</p><p>(9) in proceeds which is perfected under § <a href="http://law.lis.virginia.gov/vacode/8.9A-315/" target="_blank" rel="noreferrer">8.9A-315</a>; or</p><p>(10) that is perfected under § <a href="http://law.lis.virginia.gov/vacode/8.9A-316/" target="_blank" rel="noreferrer">8.9A-316</a>.</p><p>(c) Assignment of perfected security interest. If a secured party assigns a perfected security interest or agricultural lien, a filing under this title is not required to continue the perfected status of the security interest against creditors of and transferees from the original debtor.</p><p>1964, c. 219, § 8.9-302; 1966, c. 569; 1973, c. 509; 1983, cc. 204, 367; 1984, c. 613; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>.</p>',15),o=[n];function s(l,c,p,d,f,g){return r(),t("div",null,o)}const v=e(a,[["render",s]]);export{h as __pageData,v as default};
