import{_ as e,o as t,c as n,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 8.9A-512 Amendment of financing statement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-512.md","filePath":"vacode/8.9A-512.md"}'),i={name:"vacode/8.9A-512.md"},o=a('<h1 id="§-8-9a-512-amendment-of-financing-statement" tabindex="-1">§ 8.9A-512 Amendment of financing statement. <a class="header-anchor" href="#§-8-9a-512-amendment-of-financing-statement" aria-label="Permalink to &quot;§ 8.9A-512 Amendment of financing statement.&quot;">​</a></h1><p>(a) Amendment of information in financing statement. Subject to § <a href="http://law.lis.virginia.gov/vacode/8.9A-509/" target="_blank" rel="noreferrer">8.9A-509</a>, a person may add or delete collateral covered by, continue or terminate the effectiveness of, or, subject to subsection (e), otherwise amend the information provided in, a financing statement by filing an amendment that:</p><p>(1) identifies, by its file number, the initial financing statement to which the amendment relates; and</p><p>(2) if the amendment relates to an initial financing statement filed or recorded in a filing office described in § <a href="http://law.lis.virginia.gov/vacode/8.9A-501/" target="_blank" rel="noreferrer">8.9A-501</a> (a) (1), provides the information specified in § <a href="http://law.lis.virginia.gov/vacode/8.9A-502/" target="_blank" rel="noreferrer">8.9A-502</a> (b).</p><p>(b) Period of effectiveness not affected. Except as otherwise provided in § <a href="http://law.lis.virginia.gov/vacode/8.9A-515/" target="_blank" rel="noreferrer">8.9A-515</a>, the filing of an amendment does not extend the period of effectiveness of the financing statement.</p><p>(c) Effectiveness of amendment adding collateral. A financing statement that is amended by an amendment that adds collateral is effective as to the added collateral only from the date of the filing of the amendment.</p><p>(d) Effectiveness of amendment adding debtor. A financing statement that is amended by an amendment that adds a debtor is effective as to the added debtor only from the date of the filing of the amendment.</p><p>(e) Certain amendments ineffective. An amendment is ineffective to the extent it:</p><p>(1) purports to delete all debtors and fails to provide the name of a debtor to be covered by the financing statement; or</p><p>(2) purports to delete all secured parties of record and fails to provide the name of a new secured party of record.</p><p>1964, c. 219, § 8.9-402; 1966, c. 649; 1973, c. 509; 1976, c. 536; 1977, c. 539; 1978, cc. 284, 285; 1982, c. 652; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0096" target="_blank" rel="noreferrer">96</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',11),r=[o];function d(f,s,c,l,m,p){return t(),n("div",null,r)}const v=e(i,[["render",d]]);export{g as __pageData,v as default};
