import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.9A-525 Fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-525.md","filePath":"vacode/8.9A-525.md"}'),o={name:"vacode/8.9A-525.md"},i=r('<h1 id="§-8-9a-525-fees" tabindex="-1">§ 8.9A-525 Fees. <a class="header-anchor" href="#§-8-9a-525-fees" aria-label="Permalink to &quot;§ 8.9A-525 Fees.&quot;">​</a></h1><p>(a) Initial financing statement or other record; general rule. -- Except as otherwise provided in subsection (e), the fee for filing and indexing a record under this part is twenty dollars.</p><p>(b) [Repealed.]</p><p>(c) Number of names. -- The number of names required to be indexed does not affect the amount of the fee in subsection (a).</p><p>(d) Response to information request. -- The fee for responding to a request for information from the filing office, other than the State Corporation Commission, including for issuing a certificate showing whether there is on file any financing statement naming a particular debtor, is twenty dollars. The fees for responding to requests for information from the State Corporation Commission made pursuant to this title are governed by § <a href="http://law.lis.virginia.gov/vacode/12.1-21.1/" target="_blank" rel="noreferrer">12.1-21.1</a>.</p><p>(e) Record of mortgage. -- This section does not require a fee with respect to a record of a mortgage which is effective as a financing statement filed as a fixture filing or as a financing statement covering as-extracted collateral or timber to be cut under § <a href="http://law.lis.virginia.gov/vacode/8.9A-502/" target="_blank" rel="noreferrer">8.9A-502</a> (c). However, the recording and satisfaction fees that otherwise would be applicable to the record of the mortgage apply.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0071" target="_blank" rel="noreferrer">71</a>.</p>',7),n=[i];function s(f,c,l,d,h,p){return t(),a("div",null,n)}const _=e(o,[["render",s]]);export{m as __pageData,_ as default};
