import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 8.9A-609 Secured party's right to take possession after default.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-609.md","filePath":"vacode/8.9A-609.md"}`),s={name:"vacode/8.9A-609.md"},o=r('<h1 id="§-8-9a-609-secured-party-s-right-to-take-possession-after-default" tabindex="-1">§ 8.9A-609 Secured party&#39;s right to take possession after default. <a class="header-anchor" href="#§-8-9a-609-secured-party-s-right-to-take-possession-after-default" aria-label="Permalink to &quot;§ 8.9A-609 Secured party&#39;s right to take possession after default.&quot;">​</a></h1><p>(a) Possession; rendering equipment unusable; disposition on debtor&#39;s premises. After default, a secured party:</p><p>(1) may take possession of the collateral; and</p><p>(2) without removal, may render equipment unusable and dispose of collateral on a debtor&#39;s premises under § <a href="http://law.lis.virginia.gov/vacode/8.9A-610/" target="_blank" rel="noreferrer">8.9A-610</a>.</p><p>(b) Judicial and nonjudicial process. A secured party may proceed under subsection (a):</p><p>(1) pursuant to judicial process; or</p><p>(2) without judicial process, if it proceeds without breach of the peace.</p><p>(c) Assembly of collateral. If so agreed, and in any event after default, a secured party may require the debtor to assemble the collateral and make it available to the secured party at a place to be designated by the secured party which is reasonably convenient to both parties.</p><p>1964, c. 219, § 8.9-503; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',9),i=[o];function n(p,d,c,l,u,f){return a(),t("div",null,i)}const m=e(s,[["render",n]]);export{_ as __pageData,m as default};
