import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 8.9A-622 Effect of acceptance of collateral.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-622.md","filePath":"vacode/8.9A-622.md"}'),r={name:"vacode/8.9A-622.md"},c=o('<h1 id="§-8-9a-622-effect-of-acceptance-of-collateral" tabindex="-1">§ 8.9A-622 Effect of acceptance of collateral. <a class="header-anchor" href="#§-8-9a-622-effect-of-acceptance-of-collateral" aria-label="Permalink to &quot;§ 8.9A-622 Effect of acceptance of collateral.&quot;">​</a></h1><p>(a) Effect of acceptance. A secured party&#39;s acceptance of collateral in full or partial satisfaction of the obligation it secures:</p><p>(1) discharges the obligation to the extent consented to by the debtor;</p><p>(2) transfers to the secured party all of a debtor&#39;s rights in the collateral;</p><p>(3) discharges the security interest or agricultural lien that is the subject of the debtor&#39;s consent and any subordinate security interest or other subordinate lien; and</p><p>(4) terminates any other subordinate interest.</p><p>(b) Discharge of subordinate interest notwithstanding noncompliance. A subordinate interest is discharged or terminated under subsection (a), even if the secured party fails to comply with this title.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',8),n=[c];function i(s,l,f,d,p,h){return t(),a("div",null,n)}const b=e(r,[["render",i]]);export{u as __pageData,b as default};
