import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 8.9A-623 Right to redeem collateral.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-623.md","filePath":"vacode/8.9A-623.md"}'),o={name:"vacode/8.9A-623.md"},l=t('<h1 id="§-8-9a-623-right-to-redeem-collateral" tabindex="-1">§ 8.9A-623 Right to redeem collateral. <a class="header-anchor" href="#§-8-9a-623-right-to-redeem-collateral" aria-label="Permalink to &quot;§ 8.9A-623 Right to redeem collateral.&quot;">​</a></h1><p>(a) Persons that may redeem. A debtor, any secondary obligor, or any other secured party or lienholder may redeem collateral.</p><p>(b) Requirements for redemption. To redeem collateral, a person shall tender:</p><p>(1) fulfillment of all obligations secured by the collateral; and</p><p>(2) the reasonable expenses and attorney&#39;s fees described in § <a href="http://law.lis.virginia.gov/vacode/8.9A-615/" target="_blank" rel="noreferrer">8.9A-615</a> (a) (1).</p><p>(c) When redemption may occur. A redemption may occur at any time before a secured party:</p><p>(1) has collected collateral under § <a href="http://law.lis.virginia.gov/vacode/8.9A-607/" target="_blank" rel="noreferrer">8.9A-607</a>;</p><p>(2) has disposed of collateral or entered into a contract for its disposition under § <a href="http://law.lis.virginia.gov/vacode/8.9A-610/" target="_blank" rel="noreferrer">8.9A-610</a>; or</p><p>(3) has accepted collateral in full or partial satisfaction of the obligation it secures under § <a href="http://law.lis.virginia.gov/vacode/8.9A-622/" target="_blank" rel="noreferrer">8.9A-622</a>.</p><p>1964, c. 219, § 8.9-506; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',10),i=[l];function n(c,s,d,p,h,_){return a(),r("div",null,i)}const g=e(o,[["render",n]]);export{m as __pageData,g as default};
