import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 8.9A-624 Waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/8.9A-624.md","filePath":"vacode/8.9A-624.md"}'),o={name:"vacode/8.9A-624.md"},i=r('<h1 id="§-8-9a-624-waiver" tabindex="-1">§ 8.9A-624 Waiver. <a class="header-anchor" href="#§-8-9a-624-waiver" aria-label="Permalink to &quot;§ 8.9A-624 Waiver.&quot;">​</a></h1><p>(a) Waiver of disposition notification. A debtor or secondary obligor may waive the right to notification of disposition of collateral under § <a href="http://law.lis.virginia.gov/vacode/8.9A-611/" target="_blank" rel="noreferrer">8.9A-611</a> only by an agreement to that effect entered into and authenticated after default.</p><p>(b) Waiver of mandatory disposition. A debtor may waive the right to require disposition of collateral under § <a href="http://law.lis.virginia.gov/vacode/8.9A-620/" target="_blank" rel="noreferrer">8.9A-620</a> (e) only by an agreement to that effect entered into and authenticated after default.</p><p>(c) Waiver of redemption right. Except in a consumer-goods transaction, a debtor or secondary obligor may waive the right to redeem collateral under § <a href="http://law.lis.virginia.gov/vacode/8.9A-623/" target="_blank" rel="noreferrer">8.9A-623</a> only by an agreement to that effect entered into and authenticated after default.</p><p>Code 1950, § 55-93; 1964, c. 219, §§ 8.9-504, 8.9-505, 8.9-506; 1964, c. 410, § 8.9-505; 1973, c. 509, §§ 8.9-504, 8.9-505; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>.</p>',5),n=[i];function d(c,l,s,f,_,h){return a(),t("div",null,n)}const g=e(o,[["render",d]]);export{v as __pageData,g as default};
