import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 9.1-1108 Disposal of property held by Department for more than 15 years.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-1108.md","filePath":"vacode/9.1-1108.md"}'),o={name:"vacode/9.1-1108.md"},i=r('<h1 id="§-9-1-1108-disposal-of-property-held-by-department-for-more-than-15-years" tabindex="-1">§ 9.1-1108 Disposal of property held by Department for more than 15 years. <a class="header-anchor" href="#§-9-1-1108-disposal-of-property-held-by-department-for-more-than-15-years" aria-label="Permalink to &quot;§ 9.1-1108 Disposal of property held by Department for more than 15 years.&quot;">​</a></h1><p>Notwithstanding the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/9.1-1106/" target="_blank" rel="noreferrer">9.1-1106</a> and <a href="http://law.lis.virginia.gov/vacode/9.1-1107/" target="_blank" rel="noreferrer">9.1-1107</a>, the Department may file an application in the Circuit Court of the City of Richmond seeking an order authorizing the disposal of all personal property, including drugs, received by the Department more than 15 years prior to the filing of the application. The application, under oath, shall list each item of property, the date of submission to the Department, the agency or individual submitting the property, any previous court orders entered regarding the storage of the property, and the proposed method of disposal. The application shall also state that written notice by first-class mail was given to each agency or individual submitting property listed at least 30 days prior to the application, and that no agency or individual objected to the disposal. A return, under oath, reporting the time, place, and manner of disposal, shall be made to the court.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>.</p>',3),n=[i];function s(p,l,h,d,c,f){return t(),a("div",null,n)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
