import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 9.1-116.1 Virginia Sexual and Domestic Violence Victim Fund; purpose; guidelines.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-116.1.md","filePath":"vacode/9.1-116.1.md"}'),n={name:"vacode/9.1-116.1.md"},s=t('<h1 id="§-9-1-116-1-virginia-sexual-and-domestic-violence-victim-fund-purpose-guidelines" tabindex="-1">§ 9.1-116.1 Virginia Sexual and Domestic Violence Victim Fund; purpose; guidelines. <a class="header-anchor" href="#§-9-1-116-1-virginia-sexual-and-domestic-violence-victim-fund-purpose-guidelines" aria-label="Permalink to &quot;§ 9.1-116.1 Virginia Sexual and Domestic Violence Victim Fund; purpose; guidelines.&quot;">​</a></h1><p>A. There is created the Virginia Sexual and Domestic Violence Victim Fund as a special nonreverting fund to be administered by the Department of Criminal Justice Services to support the prosecution of domestic violence cases and victim services.</p><p>B. The Department shall adopt guidelines, the purpose of which shall be to make funds available to (i) local attorneys for the Commonwealth for the purpose of funding the cost of additional attorneys or to further dedicate existing resources to prosecute felonies and misdemeanors involving domestic violence, sexual violence, sexual abuse, stalking, and family abuse; (ii) law-enforcement authorities or appropriate programs, including civil legal assistance, to assist in protecting and providing necessary services to victims of and children affected by domestic violence, sexual abuse, stalking, and family abuse; and (iii) sexual assault service providers and hospitals for the purpose of funding the cost of salaries and equipment for sexual assault forensic examiners, sexual assault nurse examiners, and pediatric sexual assault nurse examiners, with priority for funding such costs given to such forensic examiners and nurse examiners serving rural or underserved areas of the Commonwealth.</p><p>C. A portion of the sum collected pursuant to § <a href="/vacode/16.1-69.48:1/">16.1-69.48:1</a> as specified in that section shall be deposited into the state treasury to the credit of this Fund in addition to any other moneys appropriated, allocated or received specifically for such purpose. The Fund shall be distributed according to grant procedures adopted pursuant to this section and shall be established on the books of the Comptroller. Any funds remaining in such Fund at the end of the biennium shall not revert to the general fund but shall remain in the Fund. Interest earned on the Fund shall be credited to the Fund.</p><p>D. The Department shall establish a grant procedure to govern funds awarded for this purpose.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0375" target="_blank" rel="noreferrer">375</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0288" target="_blank" rel="noreferrer">288</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0210" target="_blank" rel="noreferrer">210</a>.</p>',6),o=[s];function r(l,d,c,u,p,h){return i(),a("div",null,o)}const g=e(n,[["render",r]]);export{m as __pageData,g as default};
