import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 9.1-128 (For contingent effective date, see Acts 2021, Sp. Sess. I, cc. 524 and 542) Dissemination of criminal history record information; Board to adopt regulations and procedures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-128.md","filePath":"vacode/9.1-128.md"}'),r={name:"vacode/9.1-128.md"},t=o('<h1 id="§-9-1-128-for-contingent-effective-date-see-acts-2021-sp-sess-i-cc-524-and-542-dissemination-of-criminal-history-record-information-board-to-adopt-regulations-and-procedures" tabindex="-1">§ 9.1-128 (For contingent effective date, see Acts 2021, Sp. Sess. I, cc. 524 and 542) Dissemination of criminal history record information; Board to adopt regulations and procedures. <a class="header-anchor" href="#§-9-1-128-for-contingent-effective-date-see-acts-2021-sp-sess-i-cc-524-and-542-dissemination-of-criminal-history-record-information-board-to-adopt-regulations-and-procedures" aria-label="Permalink to &quot;§ 9.1-128 (For contingent effective date, see Acts 2021, Sp. Sess. I, cc. 524 and 542) Dissemination of criminal history record information; Board to adopt regulations and procedures.&quot;">​</a></h1><p>A. Criminal history record information shall be disseminated, whether directly or through an intermediary, only in accordance with § <a href="/vacode/19.2-389/">19.2-389</a>.</p><p>B. The Board shall adopt regulations and procedures for the interstate dissemination of criminal history record information by which criminal justice agencies of the Commonwealth shall ensure that the limitations on dissemination of criminal history record information set forth in § <a href="/vacode/19.2-389/">19.2-389</a> are accepted by recipients and will remain operative in the event of further dissemination.</p><p>C. The Board shall adopt regulations and procedures for the validation of an interstate recipient&#39;s right to obtain criminal history record information from criminal justice agencies of the Commonwealth.</p><p>D. The Board shall adopt regulations and procedures for the dissemination of sealed criminal history record information, including any records relating to an arrest, charge, or conviction, by which the criminal justice agencies of the Commonwealth and other persons, agencies, and employers can access such sealed records and shall ensure that access to and dissemination of such sealed records are made in accordance with the limitations on dissemination and use set forth in §§ <a href="/vacode/19.2-389/">19.2-389</a>, <a href="/vacode/19.2-389.3/">19.2-389.3</a>, and <a href="/vacode/19.2-392.13/">19.2-392.13</a>.</p><p>1981, c. 632, §§ 9-187, 9-188; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0524" target="_blank" rel="noreferrer">524</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0542" target="_blank" rel="noreferrer">542</a>.</p>',6),n=[t];function s(c,d,l,h,f,m){return a(),i("div",null,n)}const u=e(r,[["render",s]]);export{g as __pageData,u as default};
