import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 9.1-135 Civil remedies for violation of this chapter or Chapter 23 of Title 19.2.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-135.md","filePath":"vacode/9.1-135.md"}'),a={name:"vacode/9.1-135.md"},r=o('<h1 id="§-9-1-135-civil-remedies-for-violation-of-this-chapter-or-chapter-23-of-title-19-2" tabindex="-1">§ 9.1-135 Civil remedies for violation of this chapter or Chapter 23 of Title 19.2. <a class="header-anchor" href="#§-9-1-135-civil-remedies-for-violation-of-this-chapter-or-chapter-23-of-title-19-2" aria-label="Permalink to &quot;§ 9.1-135 Civil remedies for violation of this chapter or Chapter 23 of Title 19.2.&quot;">​</a></h1><p>A. Any person may institute a civil action in the circuit court of the jurisdiction in which the Board has its administrative headquarters, or in the jurisdiction in which any violation is alleged to have occurred:</p><ol><li><p>For actual damages resulting from violation of this article or to restrain any such violation, or both.</p></li><li><p>To obtain appropriate equitable relief against any person who has engaged, is engaged, or is about to engage in any acts or practices in violation of Chapter 23 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-387/" target="_blank" rel="noreferrer">19.2-387</a> et seq.) of Title 19.2, this chapter or rules or regulations of the Board.</p></li></ol><p>B. This section shall not be construed as a waiver of the defense of sovereign immunity.</p><p>1981, c. 632, § 9-194; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',5),n=[r];function s(l,c,h,p,f,d){return i(),t("div",null,n)}const u=e(a,[["render",s]]);export{v as __pageData,u as default};
