import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 9.1-140 Exceptions from article; training requirements for out-of-state central station dispatchers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-140.md","filePath":"vacode/9.1-140.md"}'),a={name:"vacode/9.1-140.md"},n=t('<h1 id="§-9-1-140-exceptions-from-article-training-requirements-for-out-of-state-central-station-dispatchers" tabindex="-1">§ 9.1-140 Exceptions from article; training requirements for out-of-state central station dispatchers. <a class="header-anchor" href="#§-9-1-140-exceptions-from-article-training-requirements-for-out-of-state-central-station-dispatchers" aria-label="Permalink to &quot;§ 9.1-140 Exceptions from article; training requirements for out-of-state central station dispatchers.&quot;">​</a></h1><p>The provisions of this article shall not apply to:</p><ol><li><p>An officer or employee of the United States, the Commonwealth, or a political subdivision of either, while the officer or employee is performing his official duties;</p></li><li><p>A person, except a private investigator as defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-138/" target="_blank" rel="noreferrer">9.1-138</a>, engaged exclusively in the business of obtaining and furnishing information regarding an individual&#39;s financial rating or a person engaged in the business of a consumer reporting agency as defined by the Federal Fair Credit Reporting Act;</p></li><li><p>An attorney licensed to practice in Virginia or his employees;</p></li><li><p>The legal owner of personal property which has been sold under any security agreement while performing acts relating to the repossession of such property;</p></li><li><p>A person receiving compensation for private employment as a security officer, or receiving compensation under the terms of a contract, express or implied, as a security officer, who is also a law-enforcement officer as defined by § <a href="http://law.lis.virginia.gov/vacode/9.1-101/" target="_blank" rel="noreferrer">9.1-101</a> and employed by the Commonwealth or any of its political subdivisions;</p></li><li><p>Any person appointed under § <a href="http://law.lis.virginia.gov/vacode/46.2-2003/" target="_blank" rel="noreferrer">46.2-2003</a> or <a href="http://law.lis.virginia.gov/vacode/56-353/" target="_blank" rel="noreferrer">56-353</a> while engaged in the employment contemplated thereunder, unless they have successfully completed training mandated by the Department;</p></li><li><p>Persons who conduct investigations as a part of the services being provided as a claims adjuster, by a claims adjuster who maintains an ongoing claims adjusting business, and any natural person employed by the claims adjuster to conduct investigations for the claims adjuster as a part of the services being provided as a claims adjuster;</p></li><li><p>Any natural person otherwise required to be registered pursuant to § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> who is employed by a business that is not a private security services business for the performance of his duties for his employer. Any such employee, however, who carries a firearm and is in direct contact with the general public in the performance of his duties shall possess a valid registration with the Department as required by this article;</p></li><li><p>Persons, sometimes known as &quot;shoppers,&quot; employed to purchase goods or services solely for the purpose of determining or assessing the efficiency, loyalty, courtesy, or honesty of the employees of a business establishment;</p></li><li><p>Licensed or registered private investigators from other states entering Virginia during an investigation originating in their state of licensure or registration when the other state offers similar reciprocity to private investigators licensed and registered by the Commonwealth;</p></li><li><p>Unarmed regular employees of telephone public service companies where the regular duties of such employees consist of protecting the property of their employers and investigating the usage of telephone services and equipment furnished by their employers, their employers&#39; affiliates, and other communications common carriers;</p></li><li><p>An end user;</p></li><li><p>A material supplier who renders advice concerning the use of products sold by an electronics security business and who does not provide installation, monitoring, repair or maintenance services for electronic security equipment;</p></li><li><p>Members of the security forces who are directly employed by electric public service companies;</p></li><li><p>Any professional engineer or architect licensed in accordance with Chapter 4 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-400/" target="_blank" rel="noreferrer">54.1-400</a> et seq.) of Title 54.1 to practice in the Commonwealth, or his employees;</p></li><li><p>Any person who only performs telemarketing or schedules appointments without access to information concerning the electronic security equipment purchased by an end user;</p></li><li><p>Any certified forensic scientist employed as an expert witness for the purpose of possibly testifying as an expert witness;</p></li><li><p>Members of the security forces who are directly employed by shipyards engaged in the construction, design, overhaul or repair of nuclear vessels for the United States Navy;</p></li><li><p>An out-of-state central station dispatcher employed by a private security services business licensed by the Department provided he (i) possesses and maintains a valid license, registration, or certification as a central station dispatcher issued by the regulatory authority of the state in which he performs the monitoring duties and (ii) has submitted his fingerprints to the regulatory authority for the conduct of a national criminal history records search;</p></li><li><p>Any person, or independent contractor or employee of any person, who (i) exclusively contracts directly with an agency of the federal government to conduct background investigations and (ii) possesses credentials issued by such agency authorizing such person, subcontractor or employee to conduct background investigations;</p></li><li><p>Any person whose occupation is limited to the technical reconstruction of the cause of accidents involving motor vehicles as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>, regardless of whether the information resulting from the investigation is to be used before a court, board, officer, or investigative committee, and who is not otherwise a private investigator as defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-138/" target="_blank" rel="noreferrer">9.1-138</a>;</p></li><li><p>Retail merchants performing locksmith services, selling locks or engaged in key cutting activities conducted at the business location who do not represent themselves to the general public as locksmiths;</p></li><li><p>Law-enforcement, fire, rescue, emergency service personnel, or other persons performing locksmith services in an emergency situation without compensation and who do not represent themselves to the general public as locksmiths;</p></li><li><p>Motor vehicle dealers as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-1500/" target="_blank" rel="noreferrer">46.2-1500</a> performing locksmith services who do not represent themselves to the general public as locksmiths;</p></li><li><p>Taxicab and towing businesses performing locksmith services that do not represent themselves to the general public as locksmiths;</p></li><li><p>Contractors licensed under Chapter 11 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-1100/" target="_blank" rel="noreferrer">54.1-1100</a> et seq.) of Title 54.1 performing locksmith services when acting within the scope of such license who do not represent themselves to the general public as locksmiths;</p></li><li><p>Any contractor as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-1100/" target="_blank" rel="noreferrer">54.1-1100</a> (i) who is exempt from the licensure requirements of Chapter 11 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-1100/" target="_blank" rel="noreferrer">54.1-1100</a> et seq.) of Title 54.1, (ii) where the total value referred to in a single contract or project is less than $1,000, (iii) when the performance of locksmith services is ancillary to the work performed by such contractor, and (iv) who does not represent himself to the general public as a locksmith;</p></li><li><p>Any individual, employed by a retail merchant that also holds a private security services business license as a locksmith, where such individual&#39;s duties relating to such license are limited to key cutting and the key cutting is performed under the direct supervision of the licensee;</p></li><li><p>Any individual engaged in (i) computer or digital forensic services as defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-138/" target="_blank" rel="noreferrer">9.1-138</a> or in the acquisition, review, or analysis of digital or computer-based information, in order to obtain or furnish information for evidentiary purposes or to provide expert testimony before a court, or (ii) network or system vulnerability testing, including network scans and risk assessment and analysis of computers connected to a network;</p></li><li><p>Employees and sales representatives of a retailer of electronic security equipment, provided such employees and sales representatives (i) sell electronic security equipment at a store location, online, or by telephone, but not at the end user&#39;s premises; (ii) are not electronic security technicians; and (iii) do not have access to end user confidential information regarding the end user&#39;s electronic security equipment; or</p></li><li><p>A certified public accountant authorized to practice in the Commonwealth under Chapter 44 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-4400/" target="_blank" rel="noreferrer">54.1-4400</a> et seq.) of Title 54.1 or his employees.</p></li></ol><p>1976, c. 737, § 54-729.28; 1977, c. 376, § 54.1-1901; 1981, c. 538; 1983, c. 569; 1984, c. 375; 1988, c. 765; 1992, c. 578, § 9-183.2; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0541" target="_blank" rel="noreferrer">541</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0543" target="_blank" rel="noreferrer">543</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0576" target="_blank" rel="noreferrer">576</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0080" target="_blank" rel="noreferrer">80</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0204" target="_blank" rel="noreferrer">204</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0026" target="_blank" rel="noreferrer">26</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0388" target="_blank" rel="noreferrer">388</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0821" target="_blank" rel="noreferrer">821</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0578" target="_blank" rel="noreferrer">578</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0136" target="_blank" rel="noreferrer">136</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0638" target="_blank" rel="noreferrer">638</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0263" target="_blank" rel="noreferrer">263</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>.</p>',4),o=[n];function s(l,c,p,h,g,f){return i(),r("div",null,o)}const m=e(a,[["render",s]]);export{u as __pageData,m as default};
