import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 9.1-143 Private Security Services Advisory Board; membership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-143.md","filePath":"vacode/9.1-143.md"}'),s={name:"vacode/9.1-143.md"},t=a('<h1 id="§-9-1-143-private-security-services-advisory-board-membership" tabindex="-1">§ 9.1-143 Private Security Services Advisory Board; membership. <a class="header-anchor" href="#§-9-1-143-private-security-services-advisory-board-membership" aria-label="Permalink to &quot;§ 9.1-143 Private Security Services Advisory Board; membership.&quot;">​</a></h1><p>The Private Security Services Advisory Board is established as an advisory board within the meaning of § <a href="http://law.lis.virginia.gov/vacode/2.2-2100/" target="_blank" rel="noreferrer">2.2-2100</a>, in the executive branch of state government. The Private Security Services Advisory Board shall consist of 15 members as follows: two members shall be private investigators; two shall be representatives of electronic security businesses; two members shall be representatives of locksmith businesses; three shall be representatives of private security services businesses providing security officers, armed couriers, detector canine handlers, or security canine handlers; one shall be a representative of a private security services business providing armored car personnel; one shall be a representative of a private security services business involving personal protection specialists; one shall be a certified private security services instructor; one shall be a special conservator of the peace appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-13/" target="_blank" rel="noreferrer">19.2-13</a>; one shall be a licensed bail bondsman and one shall be a representative of law enforcement. The Private Security Services Advisory Board shall be appointed by the Criminal Justice Services Board and shall advise the Criminal Justice Services Board on all issues relating to regulation of private security services businesses.</p><p>1976, c. 737, § 54-729.30; 1977, c. 376, § 54.1-1904; 1984, cc. 57, 779; 1985, c. 448; 1988, c. 765; 1992, c. 578, § 9-183.5; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0922" target="_blank" rel="noreferrer">922</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0638" target="_blank" rel="noreferrer">638</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0375" target="_blank" rel="noreferrer">375</a>.</p>',3),o=[t];function n(l,c,v,p,h,d){return r(),i("div",null,o)}const g=e(s,[["render",n]]);export{f as __pageData,g as default};
