import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 9.1-144 Insurance required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-144.md","filePath":"vacode/9.1-144.md"}'),t={name:"vacode/9.1-144.md"},n=a('<h1 id="§-9-1-144-insurance-required" tabindex="-1">§ 9.1-144 Insurance required. <a class="header-anchor" href="#§-9-1-144-insurance-required" aria-label="Permalink to &quot;§ 9.1-144 Insurance required.&quot;">​</a></h1><p>In order for his license or certificate to be operative, any person licensed as a private security services business under subsection A of § <a href="/vacode/9.1-139/">9.1-139</a> or certified as a private security services training school under subsection B of § <a href="/vacode/9.1-139/">9.1-139</a> shall file with the Department evidence of a policy of liability insurance in an amount and with coverage as fixed by the Department. The liability insurance shall be maintained for so long as the licensee or certificate holder is licensed or certified by the Department.</p><p>Every personal protection specialist and private investigator who has been issued a registration by the Department and is hired as an independent contractor by a licensed private security services business shall maintain comprehensive general liability insurance in a reasonable amount to be fixed by the Department, evidence of which shall be provided to the private security services business prior to the hiring of such independent contractor pursuant to subsection C of § <a href="/vacode/9.1-139/">9.1-139</a>.</p><p>1976, c. 737, § 54-729.31; 1988, c. 765, § 54.1-1905; 1992, c. 578, § 9-183.6; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0122" target="_blank" rel="noreferrer">122</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0202" target="_blank" rel="noreferrer">202</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>.</p>',4),s=[n];function o(c,l,d,h,p,f){return r(),i("div",null,s)}const _=e(t,[["render",o]]);export{v as __pageData,_ as default};
