import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 9.1-148 Unlawful procurement of a license; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-148.md","filePath":"vacode/9.1-148.md"}'),a={name:"vacode/9.1-148.md"},r=n('<h1 id="§-9-1-148-unlawful-procurement-of-a-license-penalty" tabindex="-1">§ 9.1-148 Unlawful procurement of a license; penalty. <a class="header-anchor" href="#§-9-1-148-unlawful-procurement-of-a-license-penalty" aria-label="Permalink to &quot;§ 9.1-148 Unlawful procurement of a license; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person to:</p><ol><li><p>Procure, or assist another to procure, through theft, fraud or other illegal means, a registration or license, by giving to, or receiving from, any person any information, oral, written or printed, during the administration of the examination, which is intended to, or will, assist any person taking the examination in passing the examination and obtaining the required registration or license;</p></li><li><p>Attempt to procure, through theft, fraud or other illegal means, any questions intended to be used by the Department conducting the examination, or the answers to the questions;</p></li><li><p>Promise or offer any valuable or other consideration to a person having access to the questions or answers as an inducement to procure for delivery to the promisor, or any other person, a copy of any questions or answers.</p></li></ol><p>B. No person, other than a designee of the Department, shall procure or have in his possession prior to the beginning of an examination, without written authority of the Department, any question intended to be used by the Department, or receive or furnish to any person taking the examination, prior to or during the examination, any written or printed material purporting to be answers to, or aid in answering such questions;</p><p>C. If an examination is divided into separate parts, each of the parts shall be deemed an examination for the purposes of this section.</p><p>D. Any person convicted of a violation of subsections A or B shall be guilty of a Class 2 misdemeanor.</p><p>1992, c. 578, § 9-183.10; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',7),i=[r];function s(l,p,c,h,u,d){return t(),o("div",null,i)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
