import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 9.1-149 Unlicensed activity prohibited; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-149.md","filePath":"vacode/9.1-149.md"}'),a={name:"vacode/9.1-149.md"},n=t('<h1 id="§-9-1-149-unlicensed-activity-prohibited-penalty" tabindex="-1">§ 9.1-149 Unlicensed activity prohibited; penalty. <a class="header-anchor" href="#§-9-1-149-unlicensed-activity-prohibited-penalty" aria-label="Permalink to &quot;§ 9.1-149 Unlicensed activity prohibited; penalty.&quot;">​</a></h1><p>A. No person:</p><ol><li><p>Required to possess a registration under subsection C of § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> shall be employed by a private security services business, except as provided in this article, as armored car personnel, courier, armed security officer, security canine handler, personal protection specialist, private investigator, alarm respondent, central station dispatcher, electronic security sales representative or electronic security technician without possessing a valid registration.</p></li><li><p>Licensed or required to be licensed under subsection A of § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> shall employ or otherwise utilize, except as provided in this article, as armored car personnel, courier, armed security officer, security canine handler, personal protection specialist, private investigator, alarm respondent, central station dispatcher, electronic security sales representative or electronic security technician, any person not possessing a valid registration.</p></li><li><p>Required to possess an instructor certification under subsection D of § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> shall provide mandated instruction, except as provided in § <a href="http://law.lis.virginia.gov/vacode/9.1-141/" target="_blank" rel="noreferrer">9.1-141</a> and Board regulations, without possessing a valid private security instructor certification.</p></li><li><p>Certified or required to be certified as a private security services training school under subsection B of § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> shall employ or otherwise utilize, except as provided in § <a href="http://law.lis.virginia.gov/vacode/9.1-141/" target="_blank" rel="noreferrer">9.1-141</a> and Board regulations, as a private security instructor, any person not possessing a valid instructor certification.</p></li></ol><p>B. No compliance agent employed or otherwise utilized by a person licensed or required to be licensed under subsection A of § <a href="http://law.lis.virginia.gov/vacode/9.1-139/" target="_blank" rel="noreferrer">9.1-139</a> shall:</p><ol><li><p>Employ or otherwise utilize as an unarmed security officer, except as provided in this article, any individual for whom the compliance agent does not possess documentary evidence of compliance with, or exemption from, the compulsory minimum training standards established by the Board for unarmed security officers and before January 1, 2003, maintain documentary evidence that an investigation to determine suitability has been conducted.</p></li><li><p>Employ or otherwise utilize as an electronic security technician&#39;s assistant, except as provided in this article, any individual for whom the compliance agent does not possess documentary evidence of compliance with, or exemption from, the compulsory minimum training standards established by the Board for electronic security technician&#39;s assistants.</p></li></ol><p>C. Any person convicted of a violation of subsections A or B shall be guilty of a Class 1 misdemeanor.</p><p>1976, c. 737, § 54-729.34; 1980, c. 425, § 54.1-1908; 1988, cc. 48, 765; 1992, c. 578, § 9-183.9; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0122" target="_blank" rel="noreferrer">122</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0578" target="_blank" rel="noreferrer">578</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>.</p>',7),o=[n];function s(l,c,p,d,h,g){return i(),r("div",null,o)}const f=e(a,[["render",s]]);export{v as __pageData,f as default};
