import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-150.4 Unlawful conduct; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-150.4.md","filePath":"vacode/9.1-150.4.md"}'),o={name:"vacode/9.1-150.4.md"},n=r('<h1 id="§-9-1-150-4-unlawful-conduct-penalties" tabindex="-1">§ 9.1-150.4 Unlawful conduct; penalties. <a class="header-anchor" href="#§-9-1-150-4-unlawful-conduct-penalties" aria-label="Permalink to &quot;§ 9.1-150.4 Unlawful conduct; penalties.&quot;">​</a></h1><p>A. It shall be unlawful for any person to (i) misrepresent facts in an application for registration; (ii) willfully refuse to furnish the Department information or records required or requested pursuant to statute or regulation; or (iii) violate any statute or regulation governing the practice of special conservators of the peace regulated by this article or § <a href="http://law.lis.virginia.gov/vacode/19.2-13/" target="_blank" rel="noreferrer">19.2-13</a>.</p><p>B. Any person registered by the Department pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-13/" target="_blank" rel="noreferrer">19.2-13</a> who the Department or the Board determines has violated any statute or Board regulation and who is not criminally prosecuted shall be subject to a monetary penalty not to exceed $2,500 for each violation. The penalty may be sued for and recovered in the name of the Commonwealth and shall be paid into the state treasury to the credit of the Literary Fund in accordance with § <a href="http://law.lis.virginia.gov/vacode/19.2-353/" target="_blank" rel="noreferrer">19.2-353</a>.</p><p>C. Any person who is convicted of a willful violation of the provisions of this article or § <a href="http://law.lis.virginia.gov/vacode/19.2-13/" target="_blank" rel="noreferrer">19.2-13</a> is guilty of a Class 1 misdemeanor. Any person convicted of a third or subsequent offense under this article or § <a href="http://law.lis.virginia.gov/vacode/19.2-13/" target="_blank" rel="noreferrer">19.2-13</a> during a 36-month period is guilty of a Class 6 felony.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0922" target="_blank" rel="noreferrer">922</a>.</p>',5),i=[n];function l(s,c,d,h,p,f){return t(),a("div",null,i)}const g=e(o,[["render",l]]);export{_ as __pageData,g as default};
