import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 9.1-151 Court-Appointed Special Advocate Program; appointment of advisory committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-151.md","filePath":"vacode/9.1-151.md"}'),o={name:"vacode/9.1-151.md"},i=r('<h1 id="§-9-1-151-court-appointed-special-advocate-program-appointment-of-advisory-committee" tabindex="-1">§ 9.1-151 Court-Appointed Special Advocate Program; appointment of advisory committee. <a class="header-anchor" href="#§-9-1-151-court-appointed-special-advocate-program-appointment-of-advisory-committee" aria-label="Permalink to &quot;§ 9.1-151 Court-Appointed Special Advocate Program; appointment of advisory committee.&quot;">​</a></h1><p>A. There is established a Court-Appointed Special Advocate Program (the Program) that shall be administered by the Department. The Program shall provide services in accordance with this article to children who are subjects of judicial proceedings (i) involving allegations that the child is abused, neglected, in need of services, or in need of supervision or (ii) for the restoration of parental rights pursuant to § <a href="/vacode/16.1-283.2/">16.1-283.2</a> and for whom the juvenile and domestic relations district court judge determines such services are appropriate. Court-Appointed Special Advocate volunteer appointments may continue for youth 18 years of age and older who are in foster care if the court has retained jurisdiction pursuant to subsection Z of § <a href="/vacode/16.1-241/">16.1-241</a> or § <a href="/vacode/16.1-242/">16.1-242</a> and the juvenile and domestic relations district court judge determines such services are appropriate. The Department shall adopt regulations necessary and appropriate for the administration of the Program.</p><p>B. The Board shall appoint an Advisory Committee to the Court-Appointed Special Advocate Program, consisting of 15 members, one of whom shall be a judge of the juvenile and domestic relations district court or circuit court, knowledgeable of court matters, child welfare, and juvenile justice issues and representative of both state and local interests. The duties of the Advisory Committee shall be to advise the Board on all matters relating to the Program and the needs of the clients served by the Program, and to make such recommendations as it may deem desirable.</p><p>1990, c. 752, § 9-173.6; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0024" target="_blank" rel="noreferrer">24</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0338" target="_blank" rel="noreferrer">338</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0685" target="_blank" rel="noreferrer">685</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0202" target="_blank" rel="noreferrer">202</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0095" target="_blank" rel="noreferrer">95</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0732" target="_blank" rel="noreferrer">732</a>.</p>',4),n=[i];function s(c,l,d,p,h,g){return t(),a("div",null,n)}const u=e(o,[["render",s]]);export{f as __pageData,u as default};
