import{_ as a,o as e,c as o,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-152 Local court-appointed special advocate programs; powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-152.md","filePath":"vacode/9.1-152.md"}'),r={name:"vacode/9.1-152.md"},i=t('<h1 id="§-9-1-152-local-court-appointed-special-advocate-programs-powers-and-duties" tabindex="-1">§ 9.1-152 Local court-appointed special advocate programs; powers and duties. <a class="header-anchor" href="#§-9-1-152-local-court-appointed-special-advocate-programs-powers-and-duties" aria-label="Permalink to &quot;§ 9.1-152 Local court-appointed special advocate programs; powers and duties.&quot;">​</a></h1><p>A. The Department shall provide a portion of any funding appropriated for this purpose to applicants seeking to establish and operate a local court-appointed special advocate program in their respective judicial districts. Only local programs operated in accordance with this article shall be eligible to receive state funds.</p><p>B. Local programs may be established and operated by local boards created for this purpose. Local boards shall ensure conformance to regulations adopted by the Board and may:</p><ol><li><p>Solicit and accept financial support from public and private sources.</p></li><li><p>Oversee the financial and program management of the local court-appointed special advocate program.</p></li><li><p>Employ and supervise a director who shall serve as a professional liaison to personnel of the court and agencies serving children.</p></li><li><p>Employ such staff as is necessary to the operation of the program.</p></li></ol><p>1990, c. 752, § 9-173.7; 1991, c. 421; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',5),p=[i];function s(c,l,n,d,h,u){return e(),o("div",null,p)}const f=a(r,[["render",s]]);export{_ as __pageData,f as default};
