import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 9.1-161 Crime prevention specialists; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-161.md","filePath":"vacode/9.1-161.md"}'),n={name:"vacode/9.1-161.md"},a=r('<h1 id="§-9-1-161-crime-prevention-specialists-duties" tabindex="-1">§ 9.1-161 Crime prevention specialists; duties. <a class="header-anchor" href="#§-9-1-161-crime-prevention-specialists-duties" aria-label="Permalink to &quot;§ 9.1-161 Crime prevention specialists; duties.&quot;">​</a></h1><p>The Board shall adopt regulations establishing minimum standards for certification of crime prevention specialists. Such regulations shall require that the chief law-enforcement officer of the locality or the campus police departments of institutions of higher education established by Article 3 (§ <a href="http://law.lis.virginia.gov/vacode/23.1-809/" target="_blank" rel="noreferrer">23.1-809</a> et seq.) of Chapter 8 of Title 23.1 wherein the person serves shall approve the certification before a candidate for certification may serve as a crime prevention specialist. A crime prevention specialist shall have the duty to:</p><ol><li><p>Provide citizens living within his jurisdiction information concerning personal safety and the security of property, and other matters relating to the reduction of criminal opportunity;</p></li><li><p>Provide business establishments within his jurisdiction information concerning business and employee security, and other matters relating to reduction of criminal activity;</p></li><li><p>Provide citizens and businesses within his jurisdiction assistance in forming and maintaining neighborhood and business watch groups and other community-based crime prevention programs;</p></li><li><p>Provide assistance to other units of government within his jurisdiction in developing plans and procedures related to the reduction of criminal activity within government and the community; and</p></li><li><p>Promote the reduction and prevention of crime within his jurisdiction and the Commonwealth.</p></li></ol><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0060" target="_blank" rel="noreferrer">60</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, § 9-173.19; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0466" target="_blank" rel="noreferrer">466</a>.</p>',4),o=[a];function s(c,l,p,h,d,f){return i(),t("div",null,o)}const g=e(n,[["render",s]]);export{u as __pageData,g as default};
