import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-177 Form of oath of office for local community-based probation officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-177.md","filePath":"vacode/9.1-177.md"}'),r={name:"vacode/9.1-177.md"},i=t('<h1 id="§-9-1-177-form-of-oath-of-office-for-local-community-based-probation-officers" tabindex="-1">§ 9.1-177 Form of oath of office for local community-based probation officers. <a class="header-anchor" href="#§-9-1-177-form-of-oath-of-office-for-local-community-based-probation-officers" aria-label="Permalink to &quot;§ 9.1-177 Form of oath of office for local community-based probation officers.&quot;">​</a></h1><p>Every local community-based probation officer who is an employee of a local community-based probation agency, established by any city, county or combination thereof, or operated pursuant to this article, that provides probation and related services pursuant to the requirements of this article, shall take an oath of office as prescribed in § <a href="http://law.lis.virginia.gov/vacode/49-1/" target="_blank" rel="noreferrer">49-1</a> before entering the duties of his office. The oath of office shall be taken before any general district or circuit court judge in any city or county that has established services for the judicial system pursuant to this article.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1040" target="_blank" rel="noreferrer">1040</a>, § 53.1-182.1:1; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0133" target="_blank" rel="noreferrer">133</a>.</p>',3),c=[i];function f(n,s,l,h,d,p){return o(),a("div",null,c)}const b=e(r,[["render",f]]);export{_ as __pageData,b as default};
