import{_ as i,o as r,c as t,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-185.2 Powers of the Criminal Justice Services Board relating to bail bondsmen.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-185.2.md","filePath":"vacode/9.1-185.2.md"}'),o={name:"vacode/9.1-185.2.md"},n=e("h1",{id:"§-9-1-185-2-powers-of-the-criminal-justice-services-board-relating-to-bail-bondsmen",tabindex:"-1"},[a("§ 9.1-185.2 Powers of the Criminal Justice Services Board relating to bail bondsmen. "),e("a",{class:"header-anchor",href:"#§-9-1-185-2-powers-of-the-criminal-justice-services-board-relating-to-bail-bondsmen","aria-label":'Permalink to "§ 9.1-185.2 Powers of the Criminal Justice Services Board relating to bail bondsmen."'},"​")],-1),s=e("p",null,"The Board shall have full regulatory authority and oversight of property and surety bail bondsmen.",-1),l=e("p",null,[a("The Board shall adopt regulations that are necessary to ensure respectable, responsible, safe and effective bail bonding within the Commonwealth. The Board's regulations shall include but not be limited to regulations that (i) establish the qualifications of applicants for licensure and renewal under this article; (ii) examine, or cause to be examined, the qualifications of each applicant for licensure, including when necessary the preparation, administration, and grading of examinations; (iii) levy and collect nonrefundable fees for licensure and renewal that are sufficient to cover all expenses for administration and operation of a program of licensure; (iv) ensure continued competency and prevent deceptive or misleading practices by practitioners; (v) administer the regulatory system; (vi) provide for receipt of complaints concerning the conduct of any person whose activities are regulated by the Board; (vii) provide for investigations and appropriate disciplinary action if warranted; (viii) establish standards for professional conduct, solicitation, collateral received in the course of business, firearms training and usage, uniforms and identification, documentation and recordkeeping requirements, reporting requirements, and methods of capture for the recovery of bailees; and (ix) allow the Board to suspend, revoke or refuse to issue, reissue or renew a license for just cause. The Board shall not adopt compulsory, minimum, firearms training standards in excess of 24 hours per year for bail bondsmen. In adopting its regulations, the Board shall seek the advice of the Private Security Services Advisory Board established pursuant to § "),e("a",{href:"http://law.lis.virginia.gov/vacode/9.1-143/",target:"_blank",rel:"noreferrer"},"9.1-143"),a(".")],-1),c=e("p",null,[a("2004, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0460",target:"_blank",rel:"noreferrer"},"460"),a(".")],-1),d=[n,s,l,c];function u(h,f,p,m,v,b){return r(),t("div",null,d)}const y=i(o,[["render",u]]);export{_ as __pageData,y as default};
