import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 9.1-185.4 Limitations on licensure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-185.4.md","filePath":"vacode/9.1-185.4.md"}'),r={name:"vacode/9.1-185.4.md"},t=i('<h1 id="§-9-1-185-4-limitations-on-licensure" tabindex="-1">§ 9.1-185.4 Limitations on licensure. <a class="header-anchor" href="#§-9-1-185-4-limitations-on-licensure" aria-label="Permalink to &quot;§ 9.1-185.4 Limitations on licensure.&quot;">​</a></h1><p>A. In order to be licensed as a bail bondsman a person shall (i) be 18 years of age or older, (ii) have received a high school diploma or passed a high school equivalency examination approved by the Board of Education, and (iii) have successfully completed the bail bondsman exam required by the Board or successfully completed prior to July 1, 2005, a surety bail bondsman exam required by the State Corporation Commission under former § <a href="http://law.lis.virginia.gov/vacode/38.2-1865.7/" target="_blank" rel="noreferrer">38.2-1865.7</a>.</p><p>B. The following persons are not eligible for licensure as bail bondsmen and may not be employed nor serve as the agent of a bail bondsman:</p><ol><li><p>Persons who have been convicted of a felony within the Commonwealth, any other state, or the United States, who have not been pardoned, or whose civil rights have not been restored;</p></li><li><p>Employees of a local or regional jail;</p></li><li><p>Employees of a sheriff&#39;s office;</p></li><li><p>Employees of a state or local police department;</p></li><li><p>Persons appointed as conservators of the peace pursuant to Article 4.1 (§ <a href="http://law.lis.virginia.gov/vacode/9.1-150.1/" target="_blank" rel="noreferrer">9.1-150.1</a> et seq.) of this chapter;</p></li><li><p>Employees of an office of an attorney for the Commonwealth;</p></li><li><p>Employees of the Department of Corrections, Department of Criminal Justice Services, or a local pretrial or community-based probation services agency; and</p></li><li><p>Spouses of or any persons residing in the same household as persons referred to in subdivisions 2 through 7 who are sworn officers or whose responsibilities involve direct access to records of inmates.</p></li></ol><p>C. The exclusions in subsection B shall not be construed to limit the ability of a licensed bail bondsman to employ or contract with a licensed bail enforcement agent authorized to do business in the Commonwealth.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0133" target="_blank" rel="noreferrer">133</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0438" target="_blank" rel="noreferrer">438</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>.</p>',6),n=[t];function s(l,c,p,h,d,f){return o(),a("div",null,n)}const u=e(r,[["render",s]]);export{b as __pageData,u as default};
