import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 9.1-185.6 Licenses; renewal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-185.6.md","filePath":"vacode/9.1-185.6.md"}'),r={name:"vacode/9.1-185.6.md"},i=t('<h1 id="§-9-1-185-6-licenses-renewal" tabindex="-1">§ 9.1-185.6 Licenses; renewal. <a class="header-anchor" href="#§-9-1-185-6-licenses-renewal" aria-label="Permalink to &quot;§ 9.1-185.6 Licenses; renewal.&quot;">​</a></h1><p>A. A license granted to a bondsman by the Department shall authorize such person to enter into bonds, as defined in § <a href="http://law.lis.virginia.gov/vacode/19.2-119/" target="_blank" rel="noreferrer">19.2-119</a>, in any county or city in the Commonwealth.</p><p>B. Every bail bondsman license issued pursuant to this article shall be for a term of two years.</p><p>C. A bail bondsman license may be renewed for an ensuing two-year period, upon the filing of an application in the form prescribed by the Department and payment of the nonrefundable renewal application processing fee prescribed by the Department. In addition, applicants for renewal of a bail bondsman license shall undergo a criminal history background check as set out in subdivision B 3 of § <a href="http://law.lis.virginia.gov/vacode/9.1-185.5/" target="_blank" rel="noreferrer">9.1-185.5</a> and shall provide all other documentation listed in subsections C and D of § <a href="http://law.lis.virginia.gov/vacode/9.1-185.5/" target="_blank" rel="noreferrer">9.1-185.5</a> as the Department deems appropriate.</p><p>D. On or before the first day of the month prior to the month his license is due to expire, the licensee shall make application for license renewal and shall at that time pay the renewal application fee.</p><p>E. Any license not renewed by its expiration date shall terminate on such date.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>.</p>',7),o=[i];function s(l,c,p,d,h,f){return a(),n("div",null,o)}const b=e(r,[["render",s]]);export{m as __pageData,b as default};
