import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-186.6 Licenses; renewal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-186.6.md","filePath":"vacode/9.1-186.6.md"}'),r={name:"vacode/9.1-186.6.md"},i=n('<h1 id="§-9-1-186-6-licenses-renewal" tabindex="-1">§ 9.1-186.6 Licenses; renewal. <a class="header-anchor" href="#§-9-1-186-6-licenses-renewal" aria-label="Permalink to &quot;§ 9.1-186.6 Licenses; renewal.&quot;">​</a></h1><p>A. A license granted to a bail enforcement agent by the Department shall authorize such person to engage in the business of bail recovery.</p><p>B. Every bail enforcement agent license issued pursuant to this article shall be for a term of two years.</p><p>C. A bail enforcement agent license may be renewed for an ensuing two-year period, upon the filing of an application in the form prescribed by the Department and payment of the nonrefundable renewal application processing fee prescribed by the Department. In addition, applicants for renewal of a bail enforcement agent&#39;s license shall provide all other documentation as the Department deems appropriate, including but not limited to, a criminal history background check.</p><p>D. On or before the first day of the month prior to the month his license is due to expire, the licensee shall make application for license renewal and shall at that time pay the renewal application fee.</p><p>E. Any license not renewed by its expiration date shall terminate on such date.</p><p>F. Prior to license renewal, bail enforcement agents shall be required to complete eight hours of continuing education approved by the Department.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>.</p>',8),o=[i];function s(l,c,p,h,d,f){return t(),a("div",null,o)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
