import{_ as e,o,c as r,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 9.1-502 Notice of charges; response; election to proceed under grievance procedure of local governing body.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-502.md","filePath":"vacode/9.1-502.md"}'),t={name:"vacode/9.1-502.md"},a=n('<h1 id="§-9-1-502-notice-of-charges-response-election-to-proceed-under-grievance-procedure-of-local-governing-body" tabindex="-1">§ 9.1-502 Notice of charges; response; election to proceed under grievance procedure of local governing body. <a class="header-anchor" href="#§-9-1-502-notice-of-charges-response-election-to-proceed-under-grievance-procedure-of-local-governing-body" aria-label="Permalink to &quot;§ 9.1-502 Notice of charges; response; election to proceed under grievance procedure of local governing body.&quot;">​</a></h1><p>A. Before any dismissal, demotion, suspension without pay or transfer for punitive reasons may be imposed, the following rights shall be afforded:</p><ol><li><p>The law-enforcement officer shall be notified in writing of all charges, the basis therefor, and the action which may be taken;</p></li><li><p>The law-enforcement officer shall be given an opportunity, within a reasonable time limit after the date of the written notice provided for above, to respond orally and in writing to the charges. The time limit shall be determined by the agency, but in no event shall it be less than five calendar days unless agreed to by the law-enforcement officer;</p></li><li><p>In making his response, the law-enforcement officer may be assisted by counsel at his own expense; and</p></li><li><p>The law-enforcement officer shall be given written notification of his right to initiate a grievance under the grievance procedure established by the local governing body pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/15.2-1506/" target="_blank" rel="noreferrer">15.2-1506</a> and <a href="http://law.lis.virginia.gov/vacode/15.2-1507/" target="_blank" rel="noreferrer">15.2-1507</a>. A copy of the local governing body&#39;s grievance procedure shall be provided to the law-enforcement officer upon his request.</p></li></ol><p>B. A law-enforcement officer may proceed under either the local governing body&#39;s grievance procedure or the law-enforcement officer&#39;s procedural guarantees, but not both.</p><p>1978, c. 19, § 2.1-116.4; 1987, c. 461; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',5),i=[a];function c(l,s,d,f,h,p){return o(),r("div",null,i)}const u=e(t,[["render",c]]);export{v as __pageData,u as default};
