import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 9.1-507 Chapter accords minimum rights; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-507.md","filePath":"vacode/9.1-507.md"}'),i={name:"vacode/9.1-507.md"},o=r('<h1 id="§-9-1-507-chapter-accords-minimum-rights-exception" tabindex="-1">§ 9.1-507 Chapter accords minimum rights; exception. <a class="header-anchor" href="#§-9-1-507-chapter-accords-minimum-rights-exception" aria-label="Permalink to &quot;§ 9.1-507 Chapter accords minimum rights; exception.&quot;">​</a></h1><p>A. The rights accorded law-enforcement officers in this chapter are minimum rights and all agencies, unless otherwise provided in this section, shall adopt grievance procedures that are consistent with this chapter. However, an agency may provide for additional rights of law-enforcement officers in its grievance procedure.</p><p>B. The provisions of this chapter shall not apply to any law-enforcement officer or law-enforcement agency that serves under the authority of a locality that has established a law-enforcement civilian oversight body pursuant to § <a href="/vacode/9.1-601/">9.1-601</a>.</p><p>1978, c. 19, § 2.1-116.9; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0029" target="_blank" rel="noreferrer">29</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>.</p>',4),n=[o];function c(s,h,l,p,d,f){return a(),t("div",null,n)}const g=e(i,[["render",c]]);export{_ as __pageData,g as default};
