import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 9.1-905 New residents and nonresident offenders; registration required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/9.1-905.md","filePath":"vacode/9.1-905.md"}'),r={name:"vacode/9.1-905.md"},i=o('<h1 id="§-9-1-905-new-residents-and-nonresident-offenders-registration-required" tabindex="-1">§ 9.1-905 New residents and nonresident offenders; registration required. <a class="header-anchor" href="#§-9-1-905-new-residents-and-nonresident-offenders-registration-required" aria-label="Permalink to &quot;§ 9.1-905 New residents and nonresident offenders; registration required.&quot;">​</a></h1><p>A. All persons required to register shall register within three days of establishing a residence in the Commonwealth.</p><p>B. Nonresident offenders entering the Commonwealth for an extended visit, for employment, to carry on a vocation, or as a student attending school who are required to register in their state of residence or who would be required to register if a resident of the Commonwealth shall, within three days of entering the Commonwealth for an extended visit, accepting employment or enrolling in school in the Commonwealth, be required to register and reregister in person with the local law-enforcement agency.</p><p>C. To document employment or school attendance in Virginia a person shall present proof of enrollment as a student or suitable proof of temporary employment in the Commonwealth and one photo-identification form issued by a governmental agency of the person&#39;s state of residence which contains the person&#39;s complete name, gender, date of birth and complete address.</p><p>D. For purposes of this section:</p><p>&quot;Employment&quot; and &quot;carry on a vocation&quot; include employment that is full-time or part-time for a period of time exceeding 14 days or for an aggregate period of time exceeding 30 days during any calendar year, whether financially compensated, volunteered, or for the purpose of government or educational benefit.</p><p>&quot;Extended visit&quot; means a period of visitation for any purpose in the Commonwealth of 30 days or more.</p><p>&quot;Student&quot; means a person who is enrolled on a full-time or part-time basis, in any public or private educational institution, including any secondary school, trade or professional institution, or institution of higher education.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0584" target="_blank" rel="noreferrer">584</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0603" target="_blank" rel="noreferrer">603</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0857" target="_blank" rel="noreferrer">857</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>.</p>',9),a=[i];function s(d,l,p,c,f,h){return t(),n("div",null,a)}const u=e(r,[["render",s]]);export{g as __pageData,u as default};
